<augment_code_snippet path="frontend/src/pages/ChatPage.tsx" mode="EDIT">
```tsx
import React, { useState, useRef, useEffect } from 'react';
import { 
  Layout, 
  Input, 
  Button, 
  Spin, 
  Select, 
  theme, 
  Typography, 
  Space, 
  Divider 
} from 'antd';
import { 
  SendOutlined, 
  RobotOutlined, 
  UserOutlined, 
  SettingOutlined 
} from '@ant-design/icons';
import ReactMarkdown from 'react-markdown';
import rehypeHighlight from 'rehype-highlight';
import { PageContainer, ProCard } from '@ant-design/pro-components';
import './ChatPage.less';

const { Header, Content, Sider } = Layout;
const { TextArea } = Input;
const { Title, Text } = Typography;
const { Option } = Select;

interface Message {
  role: 'user' | 'assistant';
  content: string;
}

const ChatPage: React.FC = () => {
  const { token } = theme.useToken();
  const [messages, setMessages] = useState<Message[]>([]);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);
  const [systemMessage, setSystemMessage] = useState('你是一个智能AI助手，擅长回答各种问题。');
  const messagesEndRef = useRef<HTMLDivElement>(null);
  const [collapsed, setCollapsed] = useState(false);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleSend = async () => {
    if (!input.trim()) return;

    const userMessage: Message = { role: 'user', content: input };
    setMessages([...messages, userMessage]);
    setInput('');
    setLoading(true);

    try {
      const eventSource = new EventSource(`http://localhost:8000/api/chat?message=${encodeURIComponent(input)}&system_message=${encodeURIComponent(systemMessage)}`);

      let assistantMessage: Message = { role: 'assistant', content: '' };
      setMessages(prev => [...prev, assistantMessage]);

      eventSource.onmessage = (event) => {
        const data = event.data;
        assistantMessage = { ...assistantMessage, content: assistantMessage.content + data };
        setMessages(prev => [...prev.slice(0, -1), assistantMessage]);
      };

      eventSource.addEventListener('done', () => {
        eventSource.close();
        setLoading(false);
      });

      eventSource.addEventListener('error', () => {
        eventSource.close();
        setLoading(false);
      });
    } catch (error) {
      console.error('Error:', error);
      setLoading(false);
    }
  };

  const handleKeyDown = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSend();
    }
  };

  return (
    <Layout style={{ minHeight: '100vh' }}>
      <Sider 
        collapsible 
        collapsed={collapsed} 
        onCollapse={setCollapsed}
        theme="light"
        style={{
          boxShadow: '0 2px 8px rgba(0, 0, 0, 0.15)',
          zIndex: 1,
        }}
      >
        <div style={{ padding: 16, textAlign: 'center' }}>
          <Space direction="vertical" size="large">
            <Title level={4} style={{ margin: 0 }}>
              <RobotOutlined /> AI Test Lab
            </Title>
            <Divider style={{ margin: '12px 0' }} />
            {!collapsed && (
              <>
                <div>
                  <Text strong>系统提示词</Text>
                  <TextArea
                    value={systemMessage}
                    onChange={(e) => setSystemMessage(e.target.value)}
                    rows={4}
                    style={{ marginTop: 8 }}
                  />
                </div>
                <Divider style={{ margin: '12px 0' }} />
                <div>
                  <Text strong>模型选择</Text>
                  <Select
                    defaultValue="deepseek-chat"
                    style={{ width: '100%', marginTop: 8 }}
                  >
                    <Option value="deepseek-chat">DeepSeek Chat</Option>
                    <Option value="gpt-4">GPT-4</Option>
                    <Option value="gpt-3.5-turbo">GPT-3.5 Turbo</Option>
                  </Select>
                </div>
              </>
            )}
          </Space>
        </div>
      </Sider>
      <Layout>
        <PageContainer
          header={{
            title: 'AI 对话助手',
            subTitle: '基于 AutoGen 的智能对话系统',
          }}
        >
          <ProCard>
            <Content style={{ padding: '0 24px', height: 'calc(100vh - 200px)', overflowY: 'auto' }}>
              <div className="chat-container">
                {messages.map((message, index) => (
                  <div
                    key={index}
                    className={`message ${message.role === 'user' ? 'user-message' : 'assistant-message'}`}
                  >
                    <div className="message-avatar">
                      {message.role === 'user' ? <UserOutlined /> : <RobotOutlined />}
                    </div>
                    <div className="message-content">
                      <ReactMarkdown rehypePlugins={[rehypeHighlight]}>
                        {message.content}
                      </ReactMarkdown>
                    </div>
                  </div>
                ))}
                <div ref={messagesEndRef} />
              </div>
            </Content>
            <div className="input-container">
              <TextArea
                value={input}
                onChange={(e) => setInput(e.target.value)}
                onKeyDown={handleKeyDown}
                placeholder="输入您的问题..."
                autoSize={{ minRows: 1, maxRows: 6 }}
                disabled={loading}
              />
              <Button
                type="primary"
                icon={<SendOutlined />}
                onClick={handleSend}
                loading={loading}
                disabled={!input.trim()}
                style={{ marginLeft: 8 }}
              >
                发送
              </Button>
            </div>
          </ProCard>
        </PageContainer>
      </Layout>
    </Layout>
  );
};

export default ChatPage;
```
</augment_code_snippet>

