````python path=backend/main.py mode=EDIT
import asyncio
from fastapi import FastAPI, Request
from fastapi.middleware.cors import CORSMiddleware
from sse_starlette.sse import EventSourceResponse
from pydantic import BaseModel
from typing import Optional

from autogen_agentchat.agents import AssistantAgent
from autogen_core.models import UserMessage, SystemMessage
from autogen_ext.models.openai import OpenAIChatCompletionClient

app = FastAPI()

# Configure CORS
app.add_middleware(
    CORSMiddleware,
    allow_origins=["*"],
    allow_credentials=True,
    allow_methods=["*"],
    allow_headers=["*"],
)

# Initialize the model client
openai_model_client = OpenAIChatCompletionClient(
    model="deepseek-chat",
    base_url="https://api.deepseek.com/v1",
    api_key="sk-df84fdd419bc469ab8c0f868f4f86374",
    model_info={
        "vision": False,
        "function_calling": True,
        "json_output": True,
        "family": "UNKNOWN",
        "structured_output": True,
        "multiple_system_messages": True,
    }
)

# Initialize the agent
agent = AssistantAgent(
    name="ai_assistant",
    model_client=openai_model_client,
    system_message="你是一个智能AI助手，擅长回答各种问题。",
    model_client_stream=True,
)

class ChatRequest(BaseModel):
    message: str
    system_message: Optional[str] = None

@app.get("/")
async def root():
    return {"message": "AI Test Lab API is running"}

@app.post("/api/chat")
async def chat(request: ChatRequest):
    async def event_generator():
        try:
            system_msg = request.system_message or "你是一个智能AI助手，擅长回答各种问题。"
            agent.system_message = system_msg

            async for chunk in agent.run_stream(task=request.message):
                if hasattr(chunk, 'content') and chunk.content:
                    yield {"event": "message", "data": chunk.content}
                await asyncio.sleep(0.01)

            yield {"event": "done", "data": ""}
        except Exception as e:
            yield {"event": "error", "data": str(e)}

    return EventSourceResponse(event_generator())

if __name__ == "__main__":
    import uvicorn
    uvicorn.run(app, host="0.0.0.0", port=8000)
