    14	@router.post("/stream")
    15	async def chat_stream(request: ChatRequest):
    16	    """流式聊天接口"""
    17	    try:
    18	        conversation_id = request.conversation_id or str(uuid.uuid4())
    19	
    20	        async def generate():
    21	            try:
    22	                async for chunk in autogen_service.chat_stream(
    23	                    message=request.message,
    24	                    conversation_id=conversation_id,
    25	                    system_message=request.system_message or "你是一个有用的AI助手"
    26	                ):
    27	                    # 发送内容块
    28	                    chunk_data = StreamChunk(
    29	                        content=chunk,
    30	                        is_complete=False,
    31	                        conversation_id=conversation_id
    32	                    )
    33	                    yield f"data: {chunk_data.model_dump_json()}\n\n"
    34	
    35	                # 发送完成信号
    36	                final_chunk = StreamChunk(
    37	                    content="",
    38	                    is_complete=True,
    39	                    conversation_id=conversation_id
    40	                )
    41	                yield f"data: {final_chunk.model_dump_json()}\n\n"
    42	
    43	            except Exception as e:
    44	                error_chunk = StreamChunk(
    45	                    content=f"错误: {str(e)}",
    46	                    is_complete=True,
    47	                    conversation_id=conversation_id
    48	                )
    49	                yield f"data: {error_chunk.model_dump_json()}\n\n"
    50	
    51	        return StreamingResponse(
    52	            generate(),
    53	            media_type="text/plain",
    54	            headers={
    55	                "Cache-Control": "no-cache",
    56	                "Connection": "keep-alive",
    57	                "Content-Type": "text/event-stream",
    58	            }
    59	        )
    60	
    61	    except Exception as e:
    62	        raise HTTPException(status_code=500, detail=str(e))
    63	
    64	
    65	@router.post("/", response_model=ChatResponse)
    66	async def chat(request: ChatRequest):
    67	    """普通聊天接口"""
    68	    try:
    69	        conversation_id = request.conversation_id or str(uuid.uuid4())
    70	        
    71	        response_message, conv_id = await autogen_service.chat(
    72	            message=request.message,
    73	            conversation_id=conversation_id,
    74	            system_message=request.system_message
    75	        )
    76	        
    77	        return ChatResponse(
    78	            message=response_message,
    79	            conversation_id=conv_id,
    80	            timestamp=datetime.now()
    81	        )
    82	        
    83	    except Exception as e:
    84	        raise HTTPException(status_code=500, detail=str(e))
    85	
    86	
    87	@router.delete("/conversation/{conversation_id}")
    88	async def clear_conversation(conversation_id: str):
    89	    """清除对话历史"""
    90	    try:
    91	        autogen_service.clear_conversation(conversation_id)
    92	        return {"message": "对话已清除", "conversation_id": conversation_id}
    93	    except Exception as e:
    94	        raise HTTPException(status_code=500, detail=str(e))
    95	
