    69	        conversation_id = request.conversation_id or str(uuid.uuid4())
    70	        
    71	        response_message, conv_id = await autogen_service.chat(
    72	            message=request.message,
    73	            conversation_id=conversation_id,
    74	            system_message=request.system_message
    75	        )
    76	        
    77	        return ChatResponse(
    78	            message=response_message,
    79	            conversation_id=conv_id,
    80	            timestamp=datetime.now()
    81	        )
    82	        
    83	    except Exception as e:
    84	        raise HTTPException(status_code=500, detail=str(e))
    85	
    86	
    87	@router.delete("/conversation/{conversation_id}")
    88	async def clear_conversation(conversation_id: str):
    89	    """清除对话历史"""
    90	    try:
    91	        autogen_service.clear_conversation(conversation_id)
    92	        return {"message": "对话已清除", "conversation_id": conversation_id}
    93	    except Exception as e:
    94	        raise HTTPException(status_code=500, detail=str(e))
