    22	import { chatApi } from '@/services/api';
    23	
    24	const { Title, Text } = Typography;
    25	
    26	const ChatPage: React.FC = () => {
    27	  const [messages, setMessages] = useState<ChatMessageType[]>([]);
    28	  const [loading, setLoading] = useState(false);
    29	  const [conversationId, setConversationId] = useState<string>('');
    30	  const [historyVisible, setHistoryVisible] = useState(false);
    31	  const [settingsVisible, setSettingsVisible] = useState(false);
    32	  const messagesEndRef = useRef<HTMLDivElement>(null);
    33	  const eventSourceRef = useRef<EventSource | null>(null);
    34	
    35	  // 滚动到底部
    36	  const scrollToBottom = () => {
    37	    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
    38	  };
    39	
    40	  useEffect(() => {
    41	    scrollToBottom();
    42	  }, [messages]);
    43	
    44	  // 初始化对话ID
    45	  useEffect(() => {
    46	    setConversationId(uuidv4());
    47	  }, []);
    48	
    49	  // 清理 EventSource
    50	  useEffect(() => {
    51	    return () => {
    52	      if (eventSourceRef.current) {
    53	        eventSourceRef.current.close();
    54	      }
    55	    };
    56	  }, []);
    57	
    58	  const handleSendMessage = async (content: string) => {
    59	    if (!content.trim()) return;
    60	
    61	    const userMessage: ChatMessageType = {
    62	      id: uuidv4(),
    63	      content,
    64	      role: 'user',
    65	      timestamp: new Date(),
    66	    };
    67	
    68	    setMessages(prev => [...prev, userMessage]);
    69	    setLoading(true);
    70	
    71	    // 创建助手消息占位符
    72	    const assistantMessageId = uuidv4();
    73	    const assistantMessage: ChatMessageType = {
    74	      id: assistantMessageId,
    75	      content: '',
    76	      role: 'assistant',
    77	      timestamp: new Date(),
    78	      isStreaming: true,
    79	    };
    80	
    81	    setMessages(prev => [...prev, assistantMessage]);
    82	
    83	    try {
    84	      // 使用流式API
    85	      const response = await fetch('/api/chat/stream', {
    86	        method: 'POST',
    87	        headers: {
    88	          'Content-Type': 'application/json',
    89	        },
    90	        body: JSON.stringify({
    91	          message: content,
    92	          conversation_id: conversationId,
    93	          system_message: '你是一个有用的AI助手，请用中文回答问题。',
    94	        }),
    95	      });
    96	
    97	      if (!response.ok) {
    98	        throw new Error('网络请求失败');
    99	      }
   100	
   101	      const reader = response.body?.getReader();
   102	      const decoder = new TextDecoder();
   103	
   104	      if (reader) {
   105	        let buffer = '';
   106	        
   107	        while (true) {
   108	          const { done, value } = await reader.read();
   109	          
   110	          if (done) break;
   111	          
   112	          buffer += decoder.decode(value, { stream: true });
   113	          const lines = buffer.split('\n');
   114	          buffer = lines.pop() || '';
   115	          
   116	          for (const line of lines) {
   117	            if (line.startsWith('data: ')) {
   118	              try {
   119	                const data = JSON.parse(line.slice(6));
   120	                const chunk: StreamChunk = data;
   121	                
   122	                if (chunk.content) {
   123	                  setMessages(prev => 
   124	                    prev.map(msg => 
   125	                      msg.id === assistantMessageId
   126	                        ? { ...msg, content: msg.content + chunk.content }
   127	                        : msg
   128	                    )
   129	                  );
   130	                }
   131	                
   132	                if (chunk.is_complete) {
   133	                  setMessages(prev => 
   134	                    prev.map(msg => 
   135	                      msg.id === assistantMessageId
   136	                        ? { ...msg, isStreaming: false }
   137	                        : msg
   138	                    )
   139	                  );
   140	                  break;
   141	                }
   142	              } catch (e) {
   143	                console.error('解析SSE数据失败:', e);
   144	              }
   145	            }
   146	          }
   147	        }
   148	      }
   149	    } catch (error) {
   150	      console.error('发送消息失败:', error);
   151	      antMessage.error('发送消息失败，请重试');
   152	      
   153	      // 移除失败的助手消息
   154	      setMessages(prev => prev.filter(msg => msg.id !== assistantMessageId));
   155	    } finally {
   156	      setLoading(false);
   157	    }
   158	  };
   159	
   160	  const handleClearChat = async () => {
   161	    try {
   162	      if (conversationId) {
   163	        await chatApi.clearConversation(conversationId);
   164	      }
   165	      setMessages([]);
   166	      setConversationId(uuidv4());
   167	      antMessage.success('对话已清除');
   168	    } catch (error) {
   169	      console.error('清除对话失败:', error);
   170	      antMessage.error('清除对话失败');
   171	    }
   172	  };
   173	
   174	  // Gemini 风格的建议卡片
   175	  const suggestionCards = [
   176	    {
   177	      icon: <BulbOutlined />,
   178	      title: "创意写作",
   179	      description: "帮我写一首关于春天的诗",
   180	      color: "#f59e0b"
   181	    },
   182	    {
   183	      icon: <CodeOutlined />,
   184	      title: "编程助手",
   185	      description: "用 Python 实现快速排序算法",
   186	      color: "#10b981"
   187	    },
   188	    {
   189	      icon: <EditOutlined />,
   190	      title: "文案优化",
   191	      description: "帮我优化这段产品介绍文案",
   192	      color: "#8b5cf6"
   193	    },
   194	    {
   195	      icon: <FileTextOutlined />,
   196	      title: "学习助手",
   197	      description: "解释一下量子计算的基本原理",
   198	      color: "#ef4444"
   199	    }
   200	  ];
   201	
