   203	    {
   204	      key: 'history',
   205	      icon: <HistoryOutlined />,
   206	      label: '对话历史',
   207	      onClick: () => setHistoryVisible(true)
   208	    },
   209	    {
   210	      key: 'clear',
   211	      icon: <ClearOutlined />,
   212	      label: '清除对话',
   213	      onClick: handleClearChat,
   214	      disabled: messages.length === 0
   215	    },
   216	    {
   217	      key: 'share',
   218	      icon: <ShareAltOutlined />,
   219	      label: '分享对话'
   220	    },
   221	    {
