   146	          >
   147	            <div style={{ width: '100%' }}>
   148	              {/* 标题 */}
   149	              <div style={{ 
   150	                display: 'flex', 
   151	                justifyContent: 'space-between', 
   152	                alignItems: 'flex-start',
   153	                marginBottom: 4
   154	              }}>
   155	                {editingId === conversation.id ? (
   156	                  <Input
   157	                    value={editTitle}
   158	                    onChange={(e) => setEditTitle(e.target.value)}
   159	                    onPressEnter={() => handleEdit(conversation.id, editTitle)}
   160	                    onBlur={() => handleEdit(conversation.id, editTitle)}
   161	                    style={{ fontSize: 14, fontWeight: 500 }}
   162	                    autoFocus
   163	                  />
   164	                ) : (
   165	                  <Text 
   166	                    style={{ 
   167	                      fontSize: 14, 
   168	                      fontWeight: 500,
   169	                      color: '#1f2937',
   170	                      lineHeight: 1.4
   171	                    }}
   172	                    ellipsis={{ tooltip: conversation.title }}
   173	                  >
   174	                    {conversation.title}
   175	                  </Text>
   176	                )}
   177	                
   178	                <Space size={4}>
   179	                  <Button
   180	                    type="text"
   181	                    size="small"
   182	                    icon={<EditOutlined />}
   183	                    onClick={(e) => {
   184	                      e.stopPropagation();
   185	                      startEdit(conversation);
   186	                    }}
   187	                    style={{ 
   188	                      color: '#6b7280',
   189	                      padding: 0,
   190	                      width: 20,
   191	                      height: 20
   192	                    }}
   193	                  />
   194	                  <Popconfirm
   195	                    title="确定删除这个对话吗？"
   196	                    onConfirm={(e) => {
   197	                      e?.stopPropagation();
   198	                      handleDelete(conversation.id);
   199	                    }}
   200	                    onCancel={(e) => e?.stopPropagation()}
   201	                  >
   202	                    <Button
   203	                      type="text"
   204	                      size="small"
   205	                      icon={<DeleteOutlined />}
   206	                      onClick={(e) => e.stopPropagation()}
   207	                      style={{ 
   208	                        color: '#ef4444',
   209	                        padding: 0,
   210	                        width: 20,
   211	                        height: 20
   212	                      }}
   213	                    />
   214	                  </Popconfirm>
   215	                </Space>
   216	              </div>
   217	
   218	              {/* 最后一条消息 */}
   219	              <Text 
   220	                style={{ 
   221	                  fontSize: 12, 
   222	                  color: '#6b7280',
   223	                  display: 'block',
   224	                  marginBottom: 4
   225	                }}
   226	                ellipsis={{ tooltip: conversation.lastMessage }}
   227	              >
   228	                {conversation.lastMessage}
   229	              </Text>
   230	
   231	              {/* 时间和消息数量 */}
   232	              <div style={{ 
   233	                display: 'flex', 
   234	                justifyContent: 'space-between', 
   235	                alignItems: 'center' 
   236	              }}>
   237	                <Space size={4}>
   238	                  <ClockCircleOutlined style={{ fontSize: 10, color: '#9ca3af' }} />
   239	                  <Text style={{ fontSize: 10, color: '#9ca3af' }}>
   240	                    {formatTime(conversation.timestamp)}
   241	                  </Text>
   242	                </Space>
   243	                <Text style={{ fontSize: 10, color: '#9ca3af' }}>
   244	                  {conversation.messageCount} 条消息
   245	                </Text>
   246	              </div>
   247	            </div>
   248	          </List.Item>
   249	        )}
   250	      />
   251	
   252	      {filteredConversations.length === 0 && (
   253	        <div style={{
   254	          textAlign: 'center',
   255	          padding: '40px 20px',
   256	          color: '#9ca3af'
   257	        }}>
   258	          <HistoryOutlined style={{ fontSize: 32, marginBottom: 8 }} />
   259	          <div>
   260	            {searchText ? '没有找到匹配的对话' : '暂无对话历史'}
   261	          </div>
   262	        </div>
   263	      )}
   264	    </Drawer>
   265	  );
   266	};
   267	
   268	export default ConversationHistory;
