   209		@rm -f *.pid *.log
   210		@echo "✅ 强制清理完成"
   211	
   212	# 显示所有相关进程
   213	show-processes:
   214		@echo " 当前运行的相关进程:"
   215		@echo ""
   216		@echo " 后端进程:"
   217		@ps -ef | grep -E "(python.*main.py|uvicorn)" | grep -v grep || echo "  无后端进程"
   218		@echo ""
   219		@echo " 前端进程:"
   220		@ps -ef | grep -E "(npm run dev|vite|esbuild)" | grep -v grep || echo "  无前端进程"
   221		@echo ""
   222		@echo " PID 文件:"
   223		@ls -la *.pid 2>/dev/null || echo "  无 PID 文件"
   224	
   225	# 检查服务状态
   226	status:
   227		@echo " 服务状态:"
   228		@if [ -f "backend.pid" ]; then \
   229			PID=$$(cat backend.pid); \
   230			if kill -0 $$PID 2>/dev/null; then \
   231				echo " 后端服务运行中 (PID: $$PID)"; \
   232			else \
   233				echo " 后端服务已停止"; \
   234				rm -f backend.pid; \
   235			fi; \
   236		else \
   237			echo " 后端服务未运行"; \
   238		fi
   239		@if [ -f "frontend.pid" ]; then \
   240			PID=$$(cat frontend.pid); \
   241			if kill -0 $$PID 2>/dev/null; then \
   242				echo " 前端服务运行中 (PID: $$PID)"; \
   243			else \
   244				echo " 前端服务已停止"; \
   245				rm -f frontend.pid; \
   246			fi; \
   247		else \
   248			echo " 前端服务未运行"; \
   249		fi
   250	
   251	# Poetry 管理命令
   252	poetry-shell:
   253		@echo " 进入 Poetry 虚拟环境..."
   254		@poetry shell
   255	
   256	poetry-show:
   257		@echo " 显示依赖信息..."
   258		@poetry show
   259	
   260	poetry-update:
   261		@echo " 更新依赖..."
   262		@poetry update
   263		@echo "✅ 依赖更新完成"
   264	
   265	# 添加依赖
   266	add-dep:
   267		@echo " 添加新依赖..."
   268		@if [ -z "$(DEP)" ]; then \
   269			echo "❌ 请指定依赖名称: make add-dep DEP=package_name"; \
   270			exit 1; \
   271		fi
   272		@poetry add $(DEP)
   273		@echo "✅ 依赖 $(DEP) 添加完成"
   274	
   275	# 添加开发依赖
   276	add-dev-dep:
   277		@echo " 添加开发依赖..."
   278		@if [ -z "$(DEP)" ]; then \
   279			echo "❌ 请指定依赖名称: make add-dev-dep DEP=package_name"; \
   280			exit 1; \
   281		fi
   282		@poetry add --group dev $(DEP)
   283		@echo "✅ 开发依赖 $(DEP) 添加完成"
   284	
   285	# 移除依赖
   286	remove-dep:
   287		@echo "️  移除依赖..."
   288		@if [ -z "$(DEP)" ]; then \
   289			echo "❌ 请指定依赖名称: make remove-dep DEP=package_name"; \
   290			exit 1; \
   291		fi
   292		@poetry remove $(DEP)
   293		@echo "✅ 依赖 $(DEP) 移除完成"
